import React, { useState } from 'react';
import { useTheme } from "@mui/material/styles";
import Swal from 'sweetalert2';
import { Search, Directions ,Storefront} from '@mui/icons-material';
//import  from '@mui/material/Search';
//import  from '@mui/material/Directions';
//import  from '@mui/material/Storefront';
import {
	IconButton,
	TextField,
	FormControl,
	InputLabel,
	Select,
	MenuItem,
	Button,
	Card,
	CardActions,
	CardContent,
	CardHeader,
	CardMedia,
	Divider,
	Grid,
	Typography,
	Paper,
	InputBase,
	Input
  } from "@mui/material";

/* const useStyles = makeStyles(theme => ({
	root: {
		'& > *': {
			margin: theme.spacing(1),
			width: '25ch'
		}
	},
	search: {
		padding: '2px 4px',
		display: 'flex',
		alignItems: 'center',
		width: 400
	},

	input: {
		marginLeft: theme.spacing(1),
		flex: 1
	},

	iconButton: {
		padding: 10
	},

	divider: {
		height: 28,
		margin: 4
	}
})); */

export const AddCategory = ({ setCategories }) => {

	const theme = useTheme();
	const cardStyle = {
	  background:
		theme.palette.mode === "dark"
		  ? theme.palette.dark.main
		  : theme.palette.grey[50],
	  border: "1px solid",
	  borderColor:
		theme.palette.mode === "dark"
		  ? theme.palette.dark.main
		  : theme.palette.grey[100],
	};
	const [inputValue, setInputValue] = useState({
		// sku: '',
		keyword: ''
	});

	const handleInputChange = event => {
		// console.log(e.target.value)

		setInputValue({
			...inputValue,
			[event.target.name]: event.target.value
		});
	};

	function onKeyDown(keyEvent) {
		if ((keyEvent.charCode || keyEvent.keyCode) === 13) {
			keyEvent.preventDefault();
		}
	}

	const handleSubmit = e => {
		e.preventDefault();
		if (inputValue.keyword.trim().length > 3) {
			setCategories({
				keyword: inputValue.keyword,
				hidden: true,
				hidden1: false,
				hidden2: true,
				selected: []
			});

			setInputValue({
				// sku: '',
				keyword: ''
			});
		} else {
			Swal.fire({
				title: 'oops!',
				text: 'Please insert a valid Keyword and SKU Reference!',
				icon: 'warning'
			});
		}
	};

	return (
		<form onKeyDown={onKeyDown} onSubmit={handleSubmit}  noValidate autoComplete="off">

				<IconButton  aria-label="menu">
					<Storefront />
				</IconButton>
				<Input
					id="keyword"
					name="keyword"
					placeholder="Insert a Product Keyword"
					value={inputValue.keyword}
					onChange={handleInputChange}
				/>
				<IconButton color="secondary" type="submit"  aria-label="search">
					<Search />
				</IconButton>
				<Divider  orientation="vertical" />
				<IconButton color="primary"  aria-label="directions">
					<Directions />
				</IconButton>

		</form>
	);
};
