import React, { useState } from "react";
import Select from "react-select";
import { useTheme } from "@mui/material/styles";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import Swal from "sweetalert2";

import { Checkbox, FormControlLabel, Grid, Typography, TextField } from '@mui/material';
const listabox = [];

const  BoxComponent = () => {

  const theme = useTheme();
  const cardStyle = {
    background:
      theme.palette.mode === "dark"
        ? theme.palette.dark.main
        : theme.palette.grey[80],
    border: "1px solid",
    borderColor:
      theme.palette.mode === "dark"
        ? theme.palette.dark.main
        : theme.palette.grey[100],
  };

  const newJson = [];

  // Relleno el Select con los FBA de Amazon

  const [box, setbox] = useState({
    id: "",
    qtybox: "",
    weight: "",
    width: "",
    length: "",
    height: "",
    weightbox: "",
    volbox: "",
    fba: "",
  });

  const [arreglobox, setarreglobox] = useState({
    arreglosdebox: [],
    totalkg: "",
    totalvol: "",
    totalqty: "",
  });

  const handleqtyChange = (event) => {
    const peso = box.qtybox * box.weight;
    const vol = (box.width * box.length * box.height * box.qtybox) / 1000000;
    const qty = parseFloat(event.target.value);
    setbox({
      ...box,
      [event.target.name]: qty,
      id: listabox.length,
      weightbox: peso,
      volbox: vol,
    });

    console.log(box);
  };

  const handleInputChange = (event) => {
    // Aviso que debe seleccionar un FBA

    setbox({
      ...box,
      fba: event.value,
    });
  };

  function onKeyDown(keyEvent) {
    if ((keyEvent.charCode || keyEvent.keyCode) === 13) {
      keyEvent.preventDefault();
    }
  }

  const submitsku = () => {
    if (
      box.qtybox !== "" &&
      box.weightbox !== "" &&
      box.weight !== "" &&
      box.width !== "" &&
      box.length !== "" &&
      box.fba !== "" &&
      box.height !== ""
    ) {
      listabox.push(box);
      let sumakg = 0;
      let sumavol = 0;
      let sumaqty = 0;
      for (const sumas of listabox) {
        sumakg = sumakg + sumas.weightbox;
        sumavol = sumavol + sumas.volbox;
        sumaqty = sumaqty + sumas.qtybox;
      }

      setarreglobox({
        ...arreglobox,
        arreglosdebox: listabox,
        totalkg: sumakg,
        totalvol: sumavol,
        totalqty: sumaqty,
      });

      console.log("PERRO: ", arreglobox.arreglosdebox);

      setbox({
        qtybox: "",
        weight: "",
        width: "",
        length: "",
        height: "",
        weightbox: "",
        volbox: "",
        fba: "",
      });
    } else {
     /* swal({
        title: "oops!",
        text: "Insert a valid Information!!",
        icon: "warning",
      });*/
    }
  };

  /* const nextbultos = () => {
    if (arreglobox.arreglosdebox !== "") {
      setdatosfinales({
        ...datosfinales,
        boxes: arreglobox,
      });

      sethidden({
        hiddenlocation: false,
        hiddensku: false,
        hiddenbox: false,
        hiddenfinal: true,
        hiddenrate: false,
      });

      console.log(arreglobox);
    } else {
      swal({
        title: "oops!",
        text: "Insert a valid Information!!",
        icon: "warning",
      });
    }
  };
*/
  // Cantidad de cajas
  var cajas = [];
  for (var i = 1; i < 6; i++) {
    cajas = [...cajas, i];
  }

  return (
    <>


        <Card sx={cardStyle} z-index = {-1}>
          <CardHeader title="" subheader=""></CardHeader>
          <CardContent>
            <Stack spacing={2} direction="row">
              <InputLabel id="qtybox">Qty Box (Max 5)</InputLabel>
              <Select
                labelId="qtybox"
                id="qtybox"
                //value={age}
                //onChange={handleChange}
                fullWidth
                //autoWidth
                label="Country"
                color="secondary"
                size="small"
                focused
              >
                {cajas.forEach((element) => (
                  <MenuItem key={element} value={element}>
                    {element}
                  </MenuItem>
                ))}
              </Select>
              <TextField
                id="peso"
                name="peso"
                label="Unit Weight"
                variant="outlined"
                color="secondary"
                type="number"
                //value={box.qtybox || ""}
                //onChange={handleqtyChange}
              />
              <TextField
                id="peso"
                name="peso"
                label="Unit Weight"
                variant="outlined"
                color="secondary"
                type="number"
                //value={box.qtybox || ""}
                //onChange={handleqtyChange}
              />
              <TextField
                id="peso"
                name="peso"
                label="Unit Weight"
                variant="outlined"
                color="secondary"
                type="number"
                //value={box.qtybox || ""}
                //onChange={handleqtyChange}
              />
              <TextField
                id="peso"
                name="peso"
                label="Unit Weight"
                variant="outlined"
                color="secondary"
                type="number"
                //value={box.qtybox || ""}
                //onChange={handleqtyChange}
              />
              <TextField
                id="peso"
                name="peso"
                label="Unit Weight"
                variant="outlined"
                color="secondary"
                type="number"
                //value={box.qtybox || ""}
                //onChange={handleqtyChange}
              />
            </Stack>
          </CardContent>
        </Card>

    </>
  );
};

export default BoxComponent;
